package com.softeem.dto;

import java.util.Date;
/**
 * ÈÕÖ¾ÎÄÕÂDTO
 * @author lx
 *
 */
public class Article {

	private int id;
	private String title;
	private String content;
	private Category category;
	private Date submitTime;
	private Date lastTime;
	private int uid;
	private int readTimes;
	private int feedbackNum;
	private String imageSrc;
	private int isHide;
	private String description;
	
	public Article() {
		// TODO Auto-generated constructor stub
	}

	public Article(int id, String title, String content, Category category, Date submitTime, Date lastTime, int uid,
			int readTimes, int feedbackNum, String imageSrc, int isHide, String description) {
		super();
		this.id = id;
		this.title = title;
		this.content = content;
		this.category = category;
		this.submitTime = submitTime;
		this.lastTime = lastTime;
		this.uid = uid;
		this.readTimes = readTimes;
		this.feedbackNum = feedbackNum;
		this.imageSrc = imageSrc;
		this.isHide = isHide;
		this.description = description;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getTitle() {
		return title;
	}

	public void setTitle(String title) {
		this.title = title;
	}

	public String getContent() {
		return content;
	}

	public void setContent(String content) {
		this.content = content;
	}

	public Date getSubmitTime() {
		return submitTime;
	}

	public void setSubmitTime(Date submitTime) {
		this.submitTime = submitTime;
	}

	public Date getLastTime() {
		return lastTime;
	}

	public void setLastTime(Date lastTime) {
		this.lastTime = lastTime;
	}

	public int getUid() {
		return uid;
	}

	public void setUid(int uid) {
		this.uid = uid;
	}

	public int getReadTimes() {
		return readTimes;
	}

	public void setReadTimes(int readTimes) {
		this.readTimes = readTimes;
	}

	public int getFeedbackNum() {
		return feedbackNum;
	}

	public void setFeedbackNum(int feedbackNum) {
		this.feedbackNum = feedbackNum;
	}

	public String getImageSrc() {
		return imageSrc;
	}

	public void setImageSrc(String imageSrc) {
		this.imageSrc = imageSrc;
	}

	public Category getCategory() {
		return category;
	}

	public void setCategory(Category category) {
		this.category = category;
	}

	public int getIsHide() {
		return isHide;
	}

	public void setIsHide(int isHide) {
		this.isHide = isHide;
	}

	public String getDescription() {
		return description;
	}

	public void setDescription(String description) {
		this.description = description;
	}
	
}
